import axios from "axios";
import React, { useState } from "react";

export default function Search() {
    const [city, setCity] = useState("");
    const [weather, setWeather] = useState("");
    const [submit, setSubmit] = useState("");

    function handleSubmit(event) {
        event.preventDefault();
        let url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=af11ba20356f076c2cd217a6bc9cd25e&units=metric`;
        axios.get(url).then(showWeather);
    }

    function updateCity(event) {
        setCity(event.target.value);
    }
    function showWeather(response) {
        setSubmit(true);
        setWeather({
            temperature: response.data.main.temp,
            wind: response.data.wind.speed,
        });
    }

    let form = (
        <form onSubmit={handleSubmit}>
            <input
                type="search"
                placeholder="Enter a city..."
                onChange={updateCity}
            />
            <input type="submit" value="Search" />
        </form>
    );

    if (submit) {
        return (
            <div>
                <h2>{city} : {Math.round(weather.temperature)}Â°C</h2>
                   <p className="detail">Wind: {Math.round(weather.wind)}m/s</p>
                {form}
            </div>
        );
    } else {
        return form;
    }
}
